import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-datepicker',
  templateUrl: './datepicker.component.html',
  styleUrls: ['./datepicker.component.css'],
})
export class DatepickerComponent {
  @Input() id: string = '';
  @Input() variant: 'date' | 'datetime' = 'date';
  @Output() onDateChange = new EventEmitter<string>();

  hangleDateChange(event: any) {
    this.onDateChange.emit(event.target.value);
    console.log('change: ', event.target.value);
  }
}
<input
  class="datepicker"
  [ngClass]="variant === 'datetime' ? 'datetime' : 'date'"
  [type]="variant === 'datetime' ? 'datetime-local' : 'date'"
  (input)="hangleDateChange($event)"
/>
